import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // For redirecting after login

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const history = useHistory(); // For redirecting after login

  const handleLogin = (e) => {
    e.preventDefault();

    fetch('http://127.0.0.1:5000/auth/login', { // Corrected endpoint
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      const token = data.access_token; // Use `access_token` based on your backend response
      localStorage.setItem('jwtToken', token); // Store the token in local storage
      history.push('/dashboard'); // Redirect to a dashboard or home page after login
    })
    .catch(error => {
      setError(error.message);
    });
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
